func (u *LiveUpsertBulk) Update(set func(*LiveUpsert)) *LiveUpsertBulk {
	u.create.conflict = append(u.create.conflict, sql.ResolveWith(func(update *sql.UpdateSet) {
		set(&LiveUpsert{UpdateSet: update})
	}))
	return u
}

